  --chart-4: 280 65% 68%;
  --chart-5: 340 75% 65%;

  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.30);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.35);
  --shadow-sm: 0px 2px 4px 0px hsl(0 0% 0% / 0.28), 0px 1px 2px -1px hsl(0 0% 0% / 0.22);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.32), 0px 2px 4px -2px hsl(0 0% 0% / 0.25);
  --shadow-md: 0px 6px 12px -2px hsl(0 0% 0% / 0.38), 0px 3px 6px -3px hsl(0 0% 0% / 0.28);
  --shadow-lg: 0px 10px 20px -3px hsl(0 0% 0% / 0.42), 0px 4px 8px -4px hsl(0 0% 0% / 0.32);
  --shadow-xl: 0px 20px 25px -5px hsl(0 0% 0% / 0.48), 0px 8px 10px -6px hsl(0 0% 0% / 0.36);
  --shadow-2xl: 0px 25px 50px -12px hsl(0 0% 0% / 0.55);

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
